import React, { useState } from 'react';
import './Modal.css';

import Login from './Login';
import Register from './Register';
import ForgotPassword from './ForgotPassword';

const Modal = ({ closeModal, setIsAuthenticated }) => {
    const [activeComponent, setActiveComponent] = useState('login');

    const renderComponent = () => {
        switch (activeComponent) {
            case 'login':
                return <Login setActiveComponent={setActiveComponent} setIsAuthenticated={setIsAuthenticated} />;
            case 'register':
                return <Register setActiveComponent={setActiveComponent} />;
            case 'forgotPassword':
                return <ForgotPassword setActiveComponent={setActiveComponent} />;
            default:
                return null;
        }
    };

    return (
        <>
            <div className="modal-overlay" onClick={closeModal}></div>
            <div className="modal">
                <button className="close-form" onClick={closeModal}>âœ–</button>
                {renderComponent()}
            </div>
        </>
    );
};

export default Modal;
