import React, { useState } from 'react';

// Конструкторы игры
import GamePartsConstructor from './GamePartsConstructor';
import GameLocationsConstructor from './GameLocationsConstructor';
import GameWizardsConstructor from './GameWizardsConstructor';
import GameWizardsDecksConstructor from './GameWizardsDecksConstructor';
import GameDarkArtsConstructor from './GameDarkArtsConstructor';
import GameVillainsConstructor from './GameVillainsConstructor';
import GameHogwartsDeckConstructor from './GameHogwartsDeckConstructor';

const container = {
    width: '80vw',
    height: '90vw',
    display: 'flex',
    flexDirection: 'row',
}

const constructorList = {
    width: '10vw',
    height: 'auto',
    display: 'flex',
    flexDirection: 'column',
}

const ConstructorGames = ({ game }) => {
    const [selectedConstructor, setSelectedConstructor] = useState(null);

    return (
        <div style={container}>
            <div style={constructorList}>
                <h2 className='constructor-components-list-title'>Компоненты конструктора</h2>
                <button className='constructor-button' onClick={() => setSelectedConstructor('parts')}>Конструктор части/главы</button>
                <button className='constructor-button' onClick={() => setSelectedConstructor('locations')}>Конструктор локаций</button>
                <button className='constructor-button' onClick={() => setSelectedConstructor('wizards')}>Конструктор волшебников</button>
                <button className='constructor-button' onClick={() => setSelectedConstructor('villains')}>Конструктор злодеев</button>
                <button className='constructor-button' onClick={() => setSelectedConstructor('darkArts')}>Конструктор темных искуств</button>
                <button className='constructor-button' onClick={() => setSelectedConstructor('wizardsDecks')}>Конструктор стартовых колод волшебников</button>
                <button className='constructor-button' onClick={() => setSelectedConstructor('hogwartsDeck')}>Конструктор колоды Хогвартса</button>
            </div>
            <div className='constructor-window-container'>
                {selectedConstructor === 'parts' && <GamePartsConstructor game={game} />}
                {selectedConstructor === 'locations' && <GameLocationsConstructor />}
                {selectedConstructor === 'wizards' && <GameWizardsConstructor />}
                {selectedConstructor === 'villains' && <GameVillainsConstructor />}
                {selectedConstructor === 'darkArts' && <GameDarkArtsConstructor />}
                {selectedConstructor === 'wizardsDecks' && <GameWizardsDecksConstructor />}
                {selectedConstructor === 'hogwartsDeck' && <GameHogwartsDeckConstructor />}
            </div>
        </div>
    );
};

export default ConstructorGames;
