import styled from 'styled-components';
import { Gamepad2, Users, Star } from 'lucide-react';

const FeaturesSectionStyled = styled.section`
  background-color: ${props => props.theme.surface};
  border-radius: 12px;
  padding: 2rem;
  margin-bottom: 3rem;
`;

const FeaturesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
  margin-top: 2rem;
`;

const FeatureCard = styled.div`
  text-align: center;
  padding: 1.5rem;
`;

const FeatureIcon = styled.div`
  background-color: ${props => props.theme.primary + '20'};
  width: 64px;
  height: 64px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  margin: 0 auto 1rem;
  svg {
    color: ${props => props.theme.primary};
    width: 32px;
    height: 32px;
  }
`;

const FeatureTitle = styled.h4`
  font-size: 1.125rem;
  font-weight: bold;
  margin-bottom: 0.5rem;
  color: ${props => props.theme.text};
`;

const FeatureDescription = styled.p`
  font-size: 0.875rem;
  color: ${props => props.theme.textSecondary};
  line-height: 1.5;
`;

const features = [
  {
    icon: Gamepad2,
    title: 'Онлайн-игры',
    description: 'Играйте в любимые настольные игры с друзьями в реальном времени.'
  },
  {
    icon: Users,
    title: 'Сообщество',
    description: 'Общайтесь, находите партнёров и делитесь впечатлениями.'
  },
  {
    icon: Star,
    title: 'Качественные переводы',
    description: 'Профессиональные переводы и локализация игр на русский язык.'
  }
];

const FeaturesSection = () => {
  return (
    <FeaturesSectionStyled>
      <FeaturesGrid>
        {features.map((feature, index) => {
          const Icon = feature.icon;
          return (
            <FeatureCard key={index}>
              <FeatureIcon>
                <Icon />
              </FeatureIcon>
              <FeatureTitle>{feature.title}</FeatureTitle>
              <FeatureDescription>{feature.description}</FeatureDescription>
            </FeatureCard>
          );
        })}
      </FeaturesGrid>
    </FeaturesSectionStyled>
  );
};

export default FeaturesSection;
