import React, { useState } from 'react';
import styled from 'styled-components';
import { useScreen } from '../../contexts/ScreenContext';

const Form = styled.form`
    display: flex;
    flex-direction: column;
    align-items: center;
`;

const Label = styled.label`
    margin-bottom: 8px;
`;

const Input = styled.input`
    margin-bottom: 16px;
    padding: 8px;
    border: 1px solid #ccc;
    border-radius: 4px;
`;

const Button = styled.button`
    margin: 4px;
    padding: 8px 16px;
    background-color: #007BFF;
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;

    &:hover {
        background-color: #0056b3;
    }
`;

const LoginForm = ({ onSubmit, onChangeFormState }) => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const { orientation, device } = useScreen();

    const handleSubmit = (event) => {
        event.preventDefault();
        onSubmit({ email, password });
    };

    return (
        <Form onSubmit={handleSubmit}>
            <div>
                <Label>Email:</Label>
                <Input 
                    type="email" 
                    value={email} 
                    onChange={(e) => setEmail(e.target.value)} 
                    required 
                    data-orientation={orientation}
                    data-device={device}
                />
            </div>
            <div>
                <Label>Password:</Label>
                <Input 
                    type="password" 
                    value={password} 
                    onChange={(e) => setPassword(e.target.value)} 
                    required 
                    data-orientation={orientation}
                    data-device={device}
                />
            </div>
            <Button type="submit">Login</Button>
            <Button type="button" onClick={() => onChangeFormState('register')}>Регистрация</Button>
            <Button type="button" onClick={() => onChangeFormState('requestReset')}>Восстановить пароль</Button>
        </Form>
    );
};

export default LoginForm;
