import React, { useState, useRef, useEffect } from 'react';
import { createPortal } from 'react-dom';
import styled from 'styled-components';

const TooltipContainer = styled.div`
  position: relative;
  display: inline-block;
`;

const TooltipText = styled.div`
  position: fixed;
  ${props => {
    switch(props.$position) {
      case 'top':
        return `
          margin-bottom: 10px;
          &::after {
            content: '';
            position: absolute;
            top: 100%;
            left: 50%;
            transform: translateX(-50%);
            border-width: 5px;
            border-style: solid;
            border-color: ${props.theme?.card || '#333'} transparent transparent transparent;
          }
        `;
      case 'bottom':
      default:
        return `
          margin-top: 10px;
          &::after {
            content: '';
            position: absolute;
            bottom: 100%;
            left: 50%;
            transform: translateX(-50%);
            border-width: 5px;
            border-style: solid;
            border-color: transparent transparent ${props.theme?.card || '#333'} transparent;
          }
        `;
    }
  }}
  background-color: ${props => props.theme?.card || '#333'};
  color: ${props => props.theme?.text || '#fff'};
  padding: 0.5rem 0.75rem;
  border-radius: 4px;
  font-size: 0.875rem;
  white-space: nowrap;
  z-index: 10000;
  opacity: ${props => props.$isVisible ? 1 : 0};
  visibility: ${props => props.$isVisible ? 'visible' : 'hidden'};
  transition: opacity 0.2s ease, visibility 0.2s ease;
  pointer-events: none;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
  transform: translate(${props => props.$x || 0}px, ${props => props.$y || 0}px);
`;

const Tooltip = ({ children, content, position = 'bottom', theme, delay = 500 }) => {
  const [visible, setVisible] = useState(false);
  const [positionStyle, setPositionStyle] = useState({ x: 0, y: 0 });
  const [timeoutId, setTimeoutId] = useState(null);
  const containerRef = useRef(null);

  const showTooltip = () => {
    const id = setTimeout(() => {
      if (containerRef.current) {
        const rect = containerRef.current.getBoundingClientRect();
        const windowWidth = window.innerWidth;
        const windowHeight = window.innerHeight;
        
        let x, y;
        
        if (position === 'top') {
          x = rect.left + rect.width / 2;
          y = rect.top;
        } else { // bottom
          x = rect.left + rect.width / 2;
          y = rect.bottom;
        }
        
        // Проверяем, не выходит ли тултип за границы экрана
        const tooltipWidth = 200; // приблизительная ширина тултипа
        if (x > windowWidth - tooltipWidth / 2) {
          x = windowWidth - tooltipWidth / 2;
        } else if (x < tooltipWidth / 2) {
          x = tooltipWidth / 2;
        }
        
        setPositionStyle({ x, y });
        setVisible(true);
      }
    }, delay);
    setTimeoutId(id);
  };

  const hideTooltip = () => {
    if (timeoutId) {
      clearTimeout(timeoutId);
    }
    setVisible(false);
  };

  return (
    <>
      <TooltipContainer
        ref={containerRef}
        onMouseEnter={showTooltip}
        onMouseLeave={hideTooltip}
      >
        {children}
      </TooltipContainer>
      {visible && content && createPortal(
        <TooltipText 
          $isVisible={visible}
          $position={position}
          $x={positionStyle.x}
          $y={positionStyle.y}
          theme={theme}
        >
          {content}
        </TooltipText>,
        document.body
      )}
    </>
  );
};

export default Tooltip;
