const EpicHeaderContainer = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
`;

const EpicLogotype = styled.img`
  height: auto;
  width: 140px;

  @media (min-width: 576px) {
    width: 100px;
  }

  @media (min-width: 768px) {
    width: 150px;
  }

  @media (min-width: 992px) {
    width: 200px;
  }

  @media (min-width: 1200px) {
    width: 250px;
  }

  @media (min-width: 1400px) {
    width: 300px;
  }
`;

const EpicHeaderButtonsContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  width: calc(90% - 100px);
  padding: 5px;
  height: 99%;
`;

const EpicButton = styled.button`
  color: #FFD700;
  border: 2px solid #707070;
  background-color: transparent;
  border-radius: 8px;
  padding: 8px 32px;
  margin: 5px 0;
  font-size: 14px;
  line-height: 20px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background-color: #FFD700;
    color: #000;
  }

  &:active {
    background-color: #e6be00;
    border-color: #505050;
  }

  &:focus {
    outline: none;
    box-shadow: 0 0 0 3px rgba(255, 215, 0, 0.5);
  }
`;

const Header = () => {
    const { user, logout, isAuthenticated } = useContext(UserContext);

    return (
        <header>
            <EpicHeaderContainer>
                <>
                    <a href="/">
                        <EpicLogotype src={logo} alt="Логотип" />
                    </a>
                    {(!user || !isAuthenticated) ? (
                        <EpicHeaderButtonsContainer>
                            <EpicButton registration>Регистрация</EpicButton>
                            <EpicButton>Войти</EpicButton>
                            <EpicButton onClick={enterFullScreen}>Развернуть на весь экран</EpicButton>
                        </EpicHeaderButtonsContainer>
                    ) : (
                        <div>test</div>
                    )}
                </>
            </EpicHeaderContainer>
        </header>
    )
}

export default Header;
