// Dashboard.js
import React, { useState } from 'react';
import User from './User';
import Menu from './Menu';
import ContentWindow from './ContentWindow';

const Dashboard = () => {
    const [selectedComponent, setSelectedComponent] = useState("games");

    return (
        <div className="container-fluid">
            <div className="row">
                <div className="col-md-3 bg-light sidebar p-3 shadow-sm" style={{ backgroundColor: 'rgba(248, 249, 250, 0.1)' }}>
                    <User />
                    <Menu selectedComponent={selectedComponent} setSelectedComponent={setSelectedComponent} />
                </div>
                <div className="col-md-9 content-window">
                    <ContentWindow selectedComponent={selectedComponent} />
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
