// ScreenContext.js
import React, { createContext, useState, useEffect, useContext } from 'react';

export const ScreenContext = createContext();

export const useScreen = () => {
    return useContext(ScreenContext);
};

export const ScreenProvider = ({ children }) => {
  const [orientation, setOrientation] = useState(window.innerWidth > window.innerHeight ? 'landscape' : 'portrait');
  const [isFullScreen, setIsFullScreen] = useState(false);
  const [containerSize, setContainerSize] = useState({ width: null, height: null });

  const calculatePlatformSize = () => {
    const { innerWidth, innerHeight } = window;
    const newOrientation = innerWidth > innerHeight ? 'landscape' : 'portrait';
    setOrientation(newOrientation);

      if (newOrientation === 'landscape') {
        const realAvailableHeight = innerHeight - window.visualViewport.offsetTop;
        const desiredWidth = realAvailableHeight * 1.54;
          
        if (desiredWidth <= innerWidth) {
          setContainerSize({
              width: desiredWidth,
              height: realAvailableHeight
          });
      } else {
          // Иначе переопределяем высоту согласно доступным данным
          const adjustedHeight = innerWidth / 1.54;
          setContainerSize({
              width: innerWidth,
              height: adjustedHeight
          });
      }
      } else {
          setContainerSize({
              width: innerWidth,
              height: innerHeight
          });
      }
  };

  useEffect(() => {
      const handleResize = () => {
          calculatePlatformSize();
      };

      const handleFullScreenChange = () => {
          setIsFullScreen(
              document.fullscreenElement ||
              document.mozFullScreenElement ||
              document.webkitFullscreenElement ||
              document.msFullscreenElement
          );
      };

      window.addEventListener('resize', handleResize);
      document.addEventListener('fullscreenchange', handleFullScreenChange);
      document.addEventListener('mozfullscreenchange', handleFullScreenChange);
      document.addEventListener('webkitfullscreenchange', handleFullScreenChange);
      document.addEventListener('MSFullscreenChange', handleFullScreenChange);

      return () => {
          window.removeEventListener('resize', handleResize);
          document.removeEventListener('fullscreenchange', handleFullScreenChange);
          document.removeEventListener('mozfullscreenchange', handleFullScreenChange);
          document.removeEventListener('webkitfullscreenchange', handleFullScreenChange);
          document.removeEventListener('MSFullscreenChange', handleFullScreenChange);
      };
  }, []);

  return (
      <ScreenContext.Provider value={{
          orientation,
          containerSize,
          isFullScreen
      }}>
          {children}
      </ScreenContext.Provider>
  );
};
