import React, { useState, useContext } from "react";
import styled from "styled-components";
import EpicGamesLogotype from '../EpicGames/EpicGamesLogotype';
import { useScreen } from '../../contexts/ScreenContext';
import { UserContext } from "../../contexts/UserContext";

const PlatformContainer = styled.div`
    display: flex;
    justify-content: flex-start;
    align-items: center;
    flex-direction: column;
    width: 100vw;
    height: 100vh;
    background-color: #15171E;
`;

const Header = styled.div`
    width: 100%;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px;
`;

const LogoMenuContainer = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
`;

const Nav = styled.nav`
    display: flex;
    justify-content: center;
    align-items: center;
`;

const MenuList = styled.ul`
    display: flex;
    list-style: none;
    padding: 0;
    margin: 0;
`;

const MenuItem = styled.li`
    font-size: 24px;
    font-weight: 600;
    color: ${({ active }) => (active ? "#FFFFFF" : "#86898E")};
    cursor: pointer;
    text-transform: uppercase;
    user-select: none;
    position: relative;
    margin: 0 15px;

    &:hover {
        color: #FFFFFF;
    }

    ${({ active }) =>
        active &&
        `
        &::after {
            content: '';
            position: absolute;
            left: 0;
            right: 0;
            bottom: -4px;
            height: 4px;
            background-color: #148EFF;
        }
    `}
`;

const Container = styled.div`
    display: flex;
    flex-direction: ${({ $orientation }) => $orientation === "landscape" ? "row" : "column"};
`;

const FirstSection = styled.div`
    display: flex;
    flex-direction: column;
`;

const SecondSection = styled.div`
    display: flex;
    flex-direction: column;
`;

const BellUserContainer = styled.div`
    display: flex;
    align-items: center;
`;

const NotificationBell = styled.div`
    // Стили для колокольчика уведомлений
`;

const FavoriteAndActiveGamesMenu = styled.div`
    // Стили для меню избранных и активных игр
`;

const ContentDisplay = styled.div`
    // Стили для отображения контента
`;

const UserHeader = styled.div`
    // Стили для информации о пользователе
`;

const FriendsBlock = styled.div`
    // Стили для блока с друзьями
`;

const ChatBlock = styled.div`
    // Стили для чата
`;

const PlatformApp = () => {
    const { orientation } = useScreen();
    const { logout } = useContext(UserContext);
    const [activeMenu, setActiveMenu] = useState('home');

    return (
        <PlatformContainer>
            <Header>
                <LogoMenuContainer>
                    <EpicGamesLogotype isLink={true} hasText={false} forTheForm={true} />
                    <Nav>
                        <MenuList>
                            <MenuItem active={activeMenu === 'home'} onClick={() => setActiveMenu('home')}>
                                Главная
                            </MenuItem>
                            <MenuItem active={activeMenu === 'games'} onClick={() => setActiveMenu('games')}>
                                Игры
                            </MenuItem>
                            <MenuItem active={activeMenu === 'store'} onClick={() => setActiveMenu('store')}>
                                Магазин
                            </MenuItem>
                        </MenuList>
                    </Nav>
                </LogoMenuContainer>
                <BellUserContainer>
                    <NotificationBell>
                        {/* Колокольчик уведомлений */}
                    </NotificationBell>
                    <UserHeader>
                        {/* Информация о пользователе */}
                    </UserHeader>
                </BellUserContainer>
            </Header>
            <Container $orientation={orientation}>
                <FirstSection $orientation={orientation}>
                    <FavoriteAndActiveGamesMenu>
                        {/* Меню избранных и активных игр */}
                    </FavoriteAndActiveGamesMenu>
                    <ContentDisplay>
                        {activeMenu === 'home' && <div>Контент для Главной</div>}
                        {activeMenu === 'games' && <div>Контент для Игр</div>}
                        {activeMenu === 'store' && <div>Контент для Магазина</div>}
                    </ContentDisplay>
                </FirstSection>
                <SecondSection $orientation={orientation}>
                    <FriendsBlock>
                        {/* Блок с друзьями */}
                    </FriendsBlock>
                    <ChatBlock>
                        {/* Чат */}
                    </ChatBlock>
                </SecondSection>
            </Container>
        </PlatformContainer>
    );
};

export default PlatformApp;
