import React, { useEffect, useState } from 'react';
import styled from 'styled-components';

const Card = styled.div`
  width: 21.28vmin;
  height: 27.57vmin;
  background-color: #24252B;
  margin-top: 1vmin;
  margin-right: 1vmin;
`;

const Poster = styled.div`
  width: 100%;
  height: 12.69vmin;
  background-size: cover;
  background-position: center;
`;

const IconAndName = styled.div`
  display: flex;
  align-items: center;
  padding: 0.5vmin;
`;

const Icon = styled.img`
  width: 3vmin;
  height: 3vmin;
  margin-right: 0.5vmin;
`;

const Description = styled.p`
  padding: 0.5vmin;
  color: white;
  font-size: 1.2vmin;
`;

const NameGame = styled.span`
    color: #86898E;
    font-size: 1.5vmin;
    font: 600;
`;

const gamesList = async () => {
    try {
        const response = await fetch(`${process.env.REACT_APP_API_URL}/api/games/games-list`, {
            headers: {
                'Content-Type': 'application/json'
            }
        });

        if (!response.ok) {
            throw new Error('Ошибка сети');
        }

        const games = await response.json();
        return games;
    } catch (error) {
        console.error('Ошибка получения списка игр:', error);
        return false;
    }
}

const EpicGamesContentGames = () => {
    const [games, setGames] = useState([]);

    useEffect(() => {
        const fetchGames = async () => {
            const gamesData = await gamesList();
            if (gamesData) {
                setGames(gamesData);
            }
        };

        fetchGames();
    }, []);

    return (
        <div>
            {games.map(game => (
                <Card key={game.id}>
                    <Poster style={{ backgroundImage: `url(${game.poster})` }} />
                    <IconAndName>
                        <Icon src={game.icon} alt={game.name} />
                        <NameGame>{game.name}</NameGame>
                    </IconAndName>
                    <Description>{game.description}</Description>
                </Card>
            ))}
        </div>
    );
};

export default EpicGamesContentGames;
