import React from "react";
import styled from "styled-components";
import EpicGamesLogotype from './EpicGamesLogotype';

import closeFormButtonSVG from '../../assets/EpicGames/images/CloseFormButton.svg';

const Modal = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: ${({ $orientation, $wMin }) => ($orientation === 'landscape' && $wMin <= 767 ? '100%' : '50%')};
    height: ${({ $orientation, $wMin }) => ($orientation === 'landscape' && $wMin <= 767 ? '100%' : '50%')};
    min-width: ${({ $wMin }) => ($wMin <= 767 ? '100%' : '280px')};
    max-width: ${({ $wMin }) => ($wMin <= 767 ? '100%' : '357px')};
    min-height: ${({ $wMin }) => ($wMin <= 767 ? '100%' : '411px')};
    max-height: ${({ $wMin }) => ($wMin <= 767 ? '100%' : '516px')};
    background-color: rgba(21, 23, 30, 0.9);
    padding: 20px;
    border-radius: ${({ $orientation, $wMin }) => ($orientation === 'landscape' && $wMin <= 767 ? '16px' : '8px')};
    z-index: 1000;
`;

const Overlay = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(40, 57, 101, 0.85);
    z-index: 999;
`;

const Header = styled.header`
    display: flex;
    justify-content: space-between;
    align-items: center;
    width: 100%;
    padding: 10px;
`;

const CloseButton = styled.button`
    width: 40px;
    height: 40px;
    background: url(${closeFormButtonSVG}) no-repeat center center;
    background-size: contain;
    border: none;
    cursor: pointer;
    transition: opacity 0.3s;

    &:hover {
        opacity: 0.7;
    }
`;

const ModalFormContainer = ({ closeModal }) => {
    return (
        <>
            <Overlay onDoubleClick={closeModal} />
            <Modal>
                <Header>
                    <EpicGamesLogotype isLink={false} hasText={true} horizontalOnly={false} />
                    <CloseButton onClick={closeModal} />
                </Header>
                <main></main>
                {/* Добавьте элементы формы здесь */}
            </Modal>
        </>
    );
};

export default ModalFormContainer;
