import React, { useState } from 'react';
import axios from 'axios';

const container = {
    width: '80%',
    height: 'auto',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center'
}

const title = {
    fontWeight: 800,
    fontSize: '32px',
    lineHeight: '50.2px',
    color: '#FFFFFF',
    textAlign: 'center',
    padding: '10px',
    paddingBottom: '30px'
}

const formContainer = {
    width: '100%',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center'
}

const Login = ({ setActiveComponent }) => {
    const [identifier, setIdentifier] = useState('');
    const [password, setPassword] = useState('');
    const [errors, setErrors] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/users/login`, { identifier, password });
            if (response.data.errors) {
                setErrors( {form: response.data.errors} );
            } else {
                localStorage.setItem('token', response.data.token);
                window.location.href = '/dashboard';
            }
        } catch (err) {
            console.error('Ошибка при авторизации:', err);
            setErrors({ form: 'Произошла ошибка при авторизации' });
        }
    };

    return (
        <div style={ container }>
            <h2 style={ title }>Войти</h2>
            <form style={ formContainer } onSubmit={handleLogin}>
                <input
                    type="text"
                    placeholder="Логин или e-mail"
                    className="input-field"
                    value={identifier}
                    onChange={(e) => setIdentifier(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder="Ваш пароль"
                    className="input-field"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <button type="submit" className="button">Войти</button>
            </form>
            <button className="button" onClick={() => setActiveComponent('register')}>Зарегистрироваться</button>
            <button className="button-forgot-password" onClick={() => setActiveComponent('forgotPassword')}>Восстановить пароль</button>
            {errors && errors.form && (
                <div className="form-errors">
                    {Array.isArray(errors.form) ? (
                        errors.form.map((error, index) => <p key={index} className="error">{error}</p>)
                    ) : (
                        <p className="error">{errors.form}</p>
                    )}
                </div>
            )}
        </div>
    );
};

export default Login;
