import './Register.css';
import React, { useState } from 'react';
import axios from 'axios';

const Register = ({ setActiveComponent }) => {
    const [login, setLogin] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [repeatPassword, setRepeatPassword] = useState('');
    const [displayName, setDisplayName] = useState('');
    const [errors, setErrors] = useState({});
    const [loading, setLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');

    const validateEmail = (email) => {
        const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return re.test(String(email).toLowerCase());
    };

    const validateLogin = (login) => {
        const re = /^[a-zA-Zа-яА-Я0-9@$!%*?&^#-_]{3,50}$/;
        return re.test(login);
    };

    const validatePassword = (password) => {
        const re = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[A-Za-z\d@$!%*?&^#-_]{8,}$/;
        return re.test(password);
    };

    const validateForm = () => {
        const newErrors = {};
        if (!login) newErrors.login = 'Логин обязателен';
        else if (!validateLogin(login)) newErrors.login = 'Некорректный логин';
        if (!email) newErrors.email = 'Email обязателен';
        else if (!validateEmail(email)) newErrors.email = 'Некорректный email';
        if (!password) newErrors.password = 'Пароль обязателен';
        else if (!validatePassword(password)) newErrors.password = 'Пароль должен содержать минимум 8 символов, включая хотя бы одну заглавную букву, одну строчную букву и одну цифру.';
        if (password !== repeatPassword) newErrors.repeatPassword = 'Пароли не совпадают';
        if (!displayName) newErrors.displayName = 'Имя пользователя обязательно';

        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validateForm()) return;

        setLoading(true);
        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/users/register`, { login, email, password, displayName });
            if (response.data && response.data.msg) {
                setSuccessMessage(response.data.msg);
                setLogin('');
                setEmail('');
                setPassword('');
                setRepeatPassword('');
                setDisplayName('');
                setErrors({});
            } else if (response.data && response.data.errors) {
                setErrors({ form: response.data.errors });
            } else {
                setErrors({ form: 'Ошибка регистрации. Пожалуйста, попробуйте снова.' });
            }
        } catch (err) {
            console.error(err);
            if (err.response && err.response.data && err.response.data.errors) {
                setErrors({ form: err.response.data.errors });
            } else {
                setErrors({ form: 'Ошибка регистрации. Пожалуйста, попробуйте снова.' });
            }
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="container">
            <h2 className="title">Зарегистрироваться</h2>
            <form className='form-container' onSubmit={handleSubmit}>
                <input
                    className="input-field"
                    type="text"
                    placeholder="Логин"
                    value={login}
                    onChange={(e) => setLogin(e.target.value)}
                />
                {errors.login && <span className="error">{errors.login}</span>}
                <input
                    className="input-field"
                    type="email"
                    placeholder="e-mail"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                {errors.email && <span className="error">{errors.email}</span>}
                <input
                    className="input-field"
                    type="password"
                    placeholder="Пароль"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                {errors.password && <span className="error">{errors.password}</span>}
                <input
                    className="input-field"
                    type="password"
                    placeholder="Повторите пароль"
                    value={repeatPassword}
                    onChange={(e) => setRepeatPassword(e.target.value)}
                />
                {errors.repeatPassword && <span className="error">{errors.repeatPassword}</span>}
                <input
                    className="input-field"
                    type="text"
                    placeholder="Имя пользователя"
                    value={displayName}
                    onChange={(e) => setDisplayName(e.target.value)}
                />
                {errors.displayName && <span className="error">{errors.displayName}</span>}

                <button type="submit" className="button" disabled={loading}>
                    Зарегистрироваться
                </button>
                {loading && <p>Загрузка...</p>}
                {successMessage && <p className="success-message">{successMessage}</p>}
                {errors.form && (
                    <div className="form-errors">
                        {Array.isArray(errors.form) ? (
                            errors.form.map((error, index) => <p key={index} className="error">{error}</p>)
                        ) : (
                            <p className="error">{errors.form}</p>
                        )}
                    </div>
                )}
            </form>
            <button className="button" onClick={() => setActiveComponent('login')}>Уже есть аккаунт</button>
            <button className="forgot-password" onClick={() => setActiveComponent('forgotPassword')}>Восстановить пароль</button>
        </div>
    );
};

export default Register;
