import React from 'react';
import styled from 'styled-components';
import { useScreen } from '../../contexts/ScreenContext';

import EpicGamesLogotypeImage from '../../assets/EpicGames/images/EpicGamesLogotype.png';
import EpicGamesLogotypeText from '../../assets/EpicGames/images/EpicGamesLogotypeText.svg';

const LogoContainer = styled.div`
  position: ${({ $forBackground }) => ($forBackground ? 'absolute' : 'relative')};
  top: ${({ $forBackground }) => ($forBackground ? '0' : 'unset')};
  left: ${({ $forBackground }) => ($forBackground ? '0' : 'unset')};
  display: flex;
  flex-direction: ${({ $orientation, $horizontalOnly, $forTheForm, $forBackground }) =>
    ($forBackground || $orientation === 'landscape' || $horizontalOnly) ? 'row' : 'column'};
  align-items: center;
  justify-content: center;
  width: ${({ $forBackground, $forTheForm, $orientation }) => {
    if ($forBackground) return '40%';
    if ($forTheForm) return '50%';
    return '50vmin';
  }};
`;

const LogoImage = styled.img`
  position: relative;
  width: ${({ $forBackground, $forTheForm, $orientation }) => {
    if ($forBackground) return '40%';
    if ($forTheForm) return '50%';
    return '10vmax';
  }};
  height: auto;
`;

const LogoText = styled.img`
  margin-left: ${({ $orientation, $forBackground }) => ($forBackground ? '0' : ($orientation === 'landscape' ? '10px' : '0'))};
  margin-top: ${({ $orientation, $forBackground }) => ($forBackground ? '0' : ($orientation === 'portrait' ? '10px' : '0'))};
  width: ${({ $forBackground, $forTheForm, $orientation }) => {
    if ($forBackground) return '60%';
    if ($forTheForm) return '100%';
    return '20vmax';
  }};
  height: auto;
`;

const EpicGamesLogotype = ({ hasText = true, isLink = false, linkUrl = '#', horizontalOnly = false, forTheForm = false, forBackground = false }) => {
  const { orientation, width } = useScreen();

  return (
    <LogoContainer $orientation={orientation} $horizontalOnly={horizontalOnly} $forTheForm={forTheForm} $forBackground={forBackground}>
      {isLink ? (
        <a href={linkUrl}>
          <LogoImage src={EpicGamesLogotypeImage} alt="Epic Games Logo" $forTheForm={forTheForm} $forBackground={forBackground} />
        </a>
      ) : (
        <LogoImage src={EpicGamesLogotypeImage} alt="Epic Games Logo" $forTheForm={forTheForm} $forBackground={forBackground} />
      )}
      {hasText && (
        <LogoText src={EpicGamesLogotypeText} alt="Epic Games Logotype Text" $orientation={orientation} $forTheForm={forTheForm} $forBackground={forBackground} />
      )}
    </LogoContainer>
  );
};

export default EpicGamesLogotype;
