import './ResetPassword.css'
import React, { useState } from 'react'
import axios from 'axios'
import { useParams, useNavigate } from 'react-router-dom'

const ResetPassword = () => {
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')
    const [message, setMessage] = useState('')
    const [error, setError] = useState('')
    const { token } = useParams()
    const navigate = useNavigate()

    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[A-Za-z\d@$!%*?&^#-_]{8,}$/

    const handleSubmit = async (e) => {
        e.preventDefault()

        if (!passwordRegex.test(password)) {
            setError('Пароль должен содержать минимум 8 символов, включать одну заглавную букву, одну строчную букву и одну цифру.')
            return
        }

        if (password !== confirmPassword) {
            setError('Пароли не совпадают')
            return
        }

        try {
            const response = await axios.post(`${process.env.REACT_APP_API_URL}/api/users/reset-password/${token}`, { password })
            setMessage(response.data.msg)
            setError('')
            // Перенаправление на страницу входа через 3 секунды
            setTimeout(() => navigate('/login'), 3000)
        } catch (err) {
            setError('Ошибка при сбросе пароля. Попробуйте еще раз.')
            setMessage('')
        }
    }

    return (
        <div>
            <h2>Восстановление пароля</h2>
            {message && <p className="success-message">{message}</p>}
            {error && <p className="error-message">{error}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Новый пароль:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label>Подтвердите новый пароль:</label>
                    <input
                        type="password"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Сбросить пароль</button>
            </form>
        </div>
    )
}

export default ResetPassword
