import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const ErrorMessage = styled.div`
    width: calc(100% - 20px);
    padding: 10px;
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    color: white;
    margin: 10px;
    font-size: 18px;
    background-color: #333333;
    border: 1px solid #666666;
    border-radius: 8px;
    position: relative;
`;

const CloseButton = styled.span`
    position: absolute;
    right: 10px;
    cursor: pointer;
    font-weight: bold;
`;

const Errors = ({ message }) => {
    const [visibleMessages, setVisibleMessages] = useState([]);

    useEffect(() => {
        if (!message) return;

        const newMessages = Array.isArray(message) ? message : [message];
        setVisibleMessages(newMessages);

        const timers = newMessages.map((_, index) =>
            setTimeout(() => closeMessage(index), 20000)
        );

        return () => timers.forEach(clearTimeout);
    }, [message]);

    const closeMessage = (index) => {
        setVisibleMessages((prevMessages) => prevMessages.filter((_, i) => i !== index));
    };

    if (visibleMessages.length === 0) return null;

    return (
        <>
            {visibleMessages.map((error, index) => (
                <ErrorMessage key={index}>
                    {error.msg || error}
                    <CloseButton onClick={() => closeMessage(index)}>Ã—</CloseButton>
                </ErrorMessage>
            ))}
        </>
    );
};

export default Errors;
