// User.js
import React, { useContext } from 'react';
import { UserContext } from '../UserContext';

const User = () => {
    const { user } = useContext(UserContext);

    return (
        <div>
            <h3>Пользователь</h3>
            {user ? <p>{user.displayName}</p> : <p>Информация о пользователе не доступна.</p>}
        </div>
    );
};

export default User;
