import React from 'react'

const ForgotPassword = ({ setActiveComponent }) => {
    return (
        <div>
            <h2>Восстановление пароля</h2>
            {/* Добавьте необходимые поля и кнопки для восстановления пароля */}
            <button onClick={() => setActiveComponent('login')}>Назад к Входу</button>
        </div>
    )
}

export default ForgotPassword

