import './Header.css';
import React, { useState } from 'react';
import Modal from './Modal';
import { useHistory } from 'react-router-dom';

const Header = ({ isAuthenticated, setIsAuthenticated }) => {
    const [isModalOpen, setIsModalOpen] = useState(false);
    const history = useHistory();

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    const handleLogout = () => {
        setIsAuthenticated(false);
        localStorage.removeItem('token'); // Удаление токена при выходе
        window.location.href = '/';
        history.push('/'); // Перенаправление на главную страницу
    };

    return (
        <header>
            <a href="/" className="logo-link">
                <div className="logo"></div>
            </a>
            {isAuthenticated ? (
                <button className="button-logout" onClick={handleLogout}>Выйти</button>
            ) : (
                <button className="button-login" onClick={openModal}>Войти</button>
            )}
            {isModalOpen && <Modal closeModal={closeModal} setIsAuthenticated={setIsAuthenticated} />}
        </header>
    );
};

export default Header;
