import React from 'react';
import styled from 'styled-components';
import { useScreen } from '../../contexts/ScreenContext';

import EpicGamesLogotypeImage from '../../assets/EpicGames/images/EpicGamesLogotype.png';
import EpicGamesLogotypeText from '../../assets/EpicGames/images/EpicGamesLogotypeText.svg';

const LogoContainer = styled.div`
  position: absolute;
  display: flex;
  flex-direction: ${({ $orientation, $horizontalOnly }) => (($orientation === 'landscape' || $horizontalOnly === true) ? 'row' : 'column')};
  align-items: center;
  width: ${({ forTheForm }) => (forTheForm ? '50%' : 'max-content')};
`;

const LogoImage = styled.img`
  width: 10vmax;
  height: auto;
  max-width: 200px;
  min-width: 100px;
`;

const LogoText = styled.img`
  margin-left: ${({ $orientation }) => ($orientation === 'landscape' ? '10px' : '0')};
  margin-top: ${({ $orientation }) => ($orientation === 'portrait' ? '10px' : '0')};
  width: 20vmax;
  max-width: 400px;
  min-width: 100px;
  height: auto;
`;

const EpicGamesLogotype = ({ hasText = true, isLink = false, linkUrl = '#', horizontalOnly = false, forTheForm = false }) => {
  const { orientation } = useScreen();

  return (
    <LogoContainer $orientation={orientation} $horizontalOnly={horizontalOnly} forTheForm={forTheForm}>
      {isLink ? (
        <a href={linkUrl}>
          <LogoImage src={EpicGamesLogotypeImage} alt="Epic Games Logo" />
        </a>
      ) : (
        <LogoImage src={EpicGamesLogotypeImage} alt="Epic Games Logo" />
      )}
      {hasText && (
        <LogoText src={EpicGamesLogotypeText} alt="Epic Games Logotype Text" $orientation={orientation} />
      )}
    </LogoContainer>
  );
};

export default EpicGamesLogotype;
