// ConstructorGames.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ConstructorGames = () => {
    const [parts, setParts] = useState([]);
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchParts = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/games/console-game/:gameId/hp_parts/get-part-list`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                if (response.data && response.data.errors) {
                    setError(response.data.errors);
                } else {
                    setParts(response.data);
                }
            } catch (err) {
                console.error('Ошибка при получении списка частей:', err);
                setError('Не удалось загрузить список частей');
            }
        };

        fetchParts();
    }, []);

    const handleAddPart = () => {
        // Логика добавления части
    };

    const handleEditPart = (partId) => {
        // Логика редактирования части
    };

    const handleDeletePart = (partId) => {
        // Логика удаления части
    };

    return (
        <div style={{ display: 'flex' }}>
            <div style={{ flex: 2, padding: '10px', borderRight: '1px solid #ccc' }}>
                <h2>Компоненты конструктора</h2>
                {/* Здесь могут быть другие компоненты конструктора */}
            </div>
            <div style={{ flex: 3, padding: '10px' }}>
                <h2>Список частей</h2>
                {error && <p className="error">{error}</p>}
                <button onClick={handleAddPart}>Создать новую часть</button>
                <ul>
                    {parts.map(part => (
                        <li key={part.id}>
                            {part.name}
                            <button onClick={() => handleEditPart(part.id)}>Изменить</button>
                            <button onClick={() => handleDeletePart(part.id)}>Удалить</button>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default ConstructorGames;
