// Games.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Games = () => {
    const [games, setGames] = useState([]);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchGames = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await axios.get(`${process.env.REACT_APP_API_URL}/api/games/games-list`, {
                    headers: { Authorization: `Bearer ${token}` }
                });
                if (response.data && response.data.errors) {
                    setError(response.data.errors);
                } else {
                    setGames(response.data);
                }
            } catch (err) {
                console.error('Ошибка при получении списка игр:', err);
                setError('Не удалось загрузить список игр');
            }
        };

        fetchGames();
    }, []);

    const handleGameClick = (game) => {
        navigate(`/console-game/${game.id}`, { state: { game } });
    };

    return (
        <div>
            <h2>Список игр</h2>
            {error && <p className="error">{error}</p>}
            <ul>
                {games.map(game => (
                    <li className='game-link' key={game.id} onClick={() => handleGameClick(game)}>
                    {game.game}
                </li>
                ))}
            </ul>
        </div>
    );
};

export default Games;
