import React from "react";
import styled from "styled-components";
import EpicGamesLogotype from './EpicGamesLogotype';

const Modal = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    position: fixed;
    width: 100%;
    height: 100%;
    background-color: rgba(21, 23, 30, 0.9);
    padding: 20px;
    border-radius: 8px;
    z-index: 1000;
`;

const Overlay = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(40, 57, 101, 0.85);
    z-index: 999;
`;

const ModalFormContainer = ({ closeModal }) => {
    return (
        <>
            <Overlay onDoubleClick={closeModal} />
            <Modal>
                <header>
                    <div>
                        <div>
                            <EpicGamesLogotype isLink={false} hasText={true} horizontalOnly={false} />
                        </div>
                        <div></div>
                    </div>
                </header>
                <main></main>
                <h2>Форма входа/регистрации</h2>
                {/* Добавьте элементы формы здесь */}
                <button onClick={closeModal}>Закрыть</button>
            </Modal>
        </>
    );
};

export default ModalFormContainer;
