// GameComponent.js
import React, { useState, useEffect } from 'react';
import ConstructorTab from './HarryPotterHogwartsBattle/ConstructorGame';
import CreateGameTab from './HarryPotterHogwartsBattle/CreateGame';
import ConnectGameTab from './HarryPotterHogwartsBattle/ConnectGame';

const GameComponent = () => {
    const [title, setTitle] = useState('');
    const [activeTab, setActiveTab] = useState('connect');

    useEffect(() => {
        // Здесь вы можете сделать запрос к серверу для получения заголовка
        setTitle('Заголовок из БД');
    }, []);

    const renderContent = () => {
        switch (activeTab) {
            case 'constructor':
                return <ConstructorTab />;
            case 'create':
                return <CreateGameTab />;
            case 'connect':
                return <ConnectGameTab />;
            default:
                return null;
        }
    };

    return (
        <div>
            <h1>{title}</h1>
            <div>
                <button onClick={() => setActiveTab('constructor')}>Конструктор</button>
                <button onClick={() => setActiveTab('create')}>Создать игру</button>
                <button onClick={() => setActiveTab('connect')}>Подключиться к игре</button>
            </div>
            <div>
                {renderContent()}
            </div>
        </div>
    );
};

export default GameComponent;
