import React from 'react';
import styled from 'styled-components';
import { useScreen } from '../../contexts/ScreenContext';

import EpicGamesLogotype from "./EpicGamesLogotype";

import EpicGamesBackgroundImg from '../../assets/EpicGames/images/EpicGamesBackground.png';

const EpicGamesBackgroundImages = styled.div`
    position: fixed;
    bottom: 0;
    width: 100%;
    height: ${({ $orientation }) => ($orientation === 'portrait' ? '95vmax' : '84vmin')};
    background: url(${EpicGamesBackgroundImg}) center/cover no-repeat;
`;

const GradientOverlay = styled.div`
  position: absolute;
  bottom: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? 'auto' : '0')};
  right: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '0' : 'auto')};
  width: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '45%' : '100%')};
  height: ${({ $orientation, $resolution }) => ($orientation === 'landscape' && $resolution.width <= 1080 ? '100%' : '35%')};
  background: ${({ $orientation, $resolution }) =>
    $orientation === 'landscape' && $resolution.width <= 1080
      ? 'linear-gradient(to right, rgba(30, 37, 56, 0) 0%, rgba(30, 37, 56, 0.25) 12%, rgba(30, 37, 56, 0.5) 25%, rgba(30, 37, 56, 1) 50%, rgba(30, 37, 56, 1) 100%)'
      : 'linear-gradient(to top, rgba(30, 37, 56, 1) 0%, rgba(30, 37, 56, 1) 50%, rgba(30, 37, 56, 0.5) 75%, rgba(30, 37, 56, 0.25) 88%, rgba(30, 37, 56, 0) 100%)'};
`;

const EpicGamesBackground = () => {
    const { orientation, resolution } = useScreen();
    
    return (
        <EpicGamesBackgroundImages $orientation={orientation}>
            <EpicGamesLogotype isLink={true} linkUrl={process.env.REACT_APP_EPIC_GAMES_URL} hasText={true} horizontalOnly={true} />
            <GradientOverlay $orientation={orientation} $resolution={resolution} />
        </EpicGamesBackgroundImages>
    )
}

export default EpicGamesBackground;
